const { stderr } = require('process')

module.exports = {
    description: 'throws exceptions',
    options: {
        number: { description: 'throw number' },
    },
    run(params) {
        if (params.options.number) {
            stderr.write('something went wrong.')
            throw 123
        } else {
            throw 'this should not happen'
        }
    },
}
